// Get all input items
const inputItems = $input.all();

// Function to sluggify a string
function sluggify(str) {
  if (!str) return '';

  // Lowercase
  str = str.toLowerCase();

  // Trim leading/trailing spaces
  str = str.trim();

  // Remove all non-word, non-space, non-hyphen characters
  str = str.replace(/[^\w\s-]/g, '');

  // Replace one or more spaces or underscores with a single hyphen
  str = str.replace(/[\s_]+/g, '-');

  // Remove leading or trailing hyphens
  str = str.replace(/^-+|-+$/g, '');

  return str;
}

// Process each item
const outputItems = inputItems.map(item => {
  const data = item.json;

  // Assume the field to sluggify is 'output'
  const projectName = data.output;

  // Add the cleaned version as 'clean_project_name'
  data.sluggified = sluggify(projectName);

  return { json: data };
});

// Return the modified items
return outputItems;

