// Run Once for All Items - filters videos and outputs only URLs
const NOW = new Date(); // current execution time

function hoursSince(dateIso) {
  const d = new Date(dateIso);
  if (isNaN(d.getTime())) return Infinity; // invalid date won't pass time-based rule
  return (NOW - d) / 36e5; // ms to hours
}

function passes(video) {
  const views = Number(video?.views ?? 0);
  const ageH = hoursSince(video?.published);
  const viewsPerHour = ageH > 0 ? views / ageH : 0;

  // ───────── NEW / FAST VIRAL (≤ 48h) ─────────
  if (ageH <= 48) {
    if (views > 7000) return true;
    if (views >= 4000 && ageH <= 12) return true;
  }

  if (ageH > 48) {
  // 1️⃣ Strong momentum (already viral)
  if (views >= 25000 && viewsPerHour >= 100) return true;

  // 2️⃣ Slow-burn breakout (still growing)
  if (views >= 15000 && viewsPerHour >= 50) return true;

  // 3️⃣ Evergreen hit (big total views, lower velocity OK)
  if (views >= 100000 && viewsPerHour >= 20) return true;

  // 4️⃣ Late spike detector (old but suddenly hot)
  if (views >= 20000 && viewsPerHour >= 80 && ageH <= 168) return true; // ≤ 7 days old
}

  return false;
}

