// Filter and map to URLs in one step
const filtered = data.filter(passes);

return filtered.map(video => {
  return {
    json: {
      url: video.url
    }
  };
});

// Input: Das geparste JSON aus der XML Node (items[0].json)
const feed = items[0].json.feed;  // Oder passe an, falls der Pfad anders ist
const entries = feed.entry || [];  // Array der Videos

// Extrahiere und mappe die Daten
const extractedVideos = entries.map(entry => {
  return {
    url: entry.link.href || '',  // Video-URL (im Beispiel ist es ein Objekt ohne @, also .href)
    title: entry.title || '',  // Titel
    views: parseInt(entry['media:group']['media:community']['media:statistics'].views || 0, 10),  // Aufrufe als Number (im Beispiel ohne @, direkt 'views')
    published: entry.published || ''  // Upload-Datum (ISO-Format)
  };
});

// Sortierung nach Published absteigend (neueste Videos zuerst)
extractedVideos.sort((a, b) => new Date(b.published) - new Date(a.published));

// Output als n8n-kompatibles Array (jeder Eintrag als separates Item)
return extractedVideos.map(video => ({ json: video }));

// Access the input data from the first item
const inputData = items[0].json.data;

// Collect existing YT links into a Set for quick lookup and uniqueness
const existingLinks = new Set();
const newUrls = [];

// Iterate through the data array to separate existing and new URLs
for (const obj of inputData) {
  if (obj["['Source Video URL']"]) {
    existingLinks.add(obj["['Source Video URL']"]);
  } else if (obj.url) {
    newUrls.push(obj.url);
  }
}

// Filter new URLs to remove those that exist in the database links,
// then use a Set to deduplicate any remaining doubles among new URLs
const filteredUniqueNew = new Set(newUrls.filter(url => !existingLinks.has(url)));

// Return the filtered unique new URLs as an array of items
return [...filteredUniqueNew].map(url => ({ json: { url } }));

// Get the input object (first item)
const inputObj = items[0].json;

// Extract all channel IDs (values) and create separate items
const channelIds = Object.values(inputObj);

return channelIds.map(channelId => {
  return {
    json: {
      "channel-id": channelId
    }
  };
});

